sys_script_include.script:
var NeedItUtils = Class.create();
NeedItUtils.prototype = {
    initialize: function() {
    },

	isDatePast: function(dateStr){
		// Create GlideDateTime objects for the current date and the passed in date
		var rightNow = new GlideDateTime();
		var testDate = new GlideDateTime(dateStr);

		// If the testdate is before rightnow, return true, else return false

		if(testDate.before(rightNow)){
			return true;
		}
		else{
			return false;
		}

	},


	isDateToday: function(dateStr){
		var todaysDate = new GlideDateTime();
		var testDate = new GlideDateTime(dateStr);

		if((todaysDate.getLocalDate()).compareTo(testDate.getLocalDate()) == 0){
			return true;

		}
		else{
			return false;
		}
	},

    type: 'NeedItUtils'
};
